const CACHE_NAME="story-app-cache-v1",urlsToCache=["/","/index.html","/manifest.json","/app.css","/app.bundle.js","/favicon.png","/images/logo.png"];self.addEventListener("install",(e=>{self.skipWaiting(),e.waitUntil(caches.open(CACHE_NAME).then((e=>e.addAll(urlsToCache))))})),self.addEventListener("activate",(e=>{self.clients.claim(),e.waitUntil(caches.keys().then((e=>Promise.all(e.map((e=>e!==CACHE_NAME?caches.delete(e):null))))))})),self.addEventListener("fetch",(e=>{const t=new URL(e.request.url);t.origin===location.origin&&t.pathname.startsWith("/v1/stories")?e.respondWith(caches.open(CACHE_NAME).then((async t=>{try{const s=await fetch(e.request);return t.put(e.request,s.clone()),s}catch{return t.match(e.request)||new Response("Offline",{status:503})}}))):e.respondWith(caches.match(e.request).then((t=>t||fetch(e.request))))})),self.addEventListener("push",(e=>{let t={title:"Story Baru!",options:{body:"Ada story baru yang dibuat.",icon:"/images/logo.png",badge:"/images/logo.png"}};if(e.data)try{t=e.data.json()}catch{t.options.body=e.data.text()}e.waitUntil(self.registration.showNotification(t.title,t.options))}));